$("#mapa").gmap().bind("init",function(){$.getJSON("js/spots",function(a){$.each(a,function(a,s){$("#mapa").gmap("addMarker",{cat_id:[s[1].cat_id,"0"],truck_id:[s[1].id,"0"],position:new google.maps.LatLng(s[0][1],s[0][0]),bounds:!0}).click(function(){$("#mapa").gmap("openInfoWindow",{maxWidth:"700",content:'<div class="map"><div class="imageMap"><img src='+s[1].logo+'/></div><div class="bodyMap"><h2>'+s[1].nome+'</h2><p class="social"><a href="truck/'+s[1].slug+'">http://foodtrucker.com.br/truck/'+s[1].slug+'</a> <br/><i class="fa fa-facebook-square fa-lg"></i> </b><a href="http://fb.com/'+s[1].facebook+'">'+s[1].facebook+'</a> <i class="fa fa-instagram fa-lg"></i> </b><a href="http://instagram.com/'+s[1].instagram+'">'+s[1].instagram+"</a></p><p>"+s.local+"</p><p>"+s.dataParsed+'</p></div><p class="servicos"> <span>'+s[2][0]+"Sobremesa</span><span>"+s[2][1]+"Bebidas</span><span>"+s[2][2]+"Música</span><span>"+s[2][3]+"Cartão de Crédito</span><span>"+s[2][4]+"Cartão de Débito</span><span>"+s[2][5]+"Vale-Refeição</span></p></div>"},this)})})})});